import './checkout-item.style.css'
import React from 'react'

import { selectCartItems } from '../../redux/cart/cart.selector'
import { connect, Connect } from 'react-redux'
import CheckoutCart from '../checkout-cart/checkout-cart.component'

import { removeItem, decreseItemQuantity, addItem} from '../../redux/cart/cart.action'

const CheckoutItem = ({cartItem, removeItem}) => {
    const {name, imageUrl, price, quantity} = cartItem;
    return (<div className='checkout-item'>

        <div className='image-container'>
            <img src={imageUrl} alt='item'/>
        </div>

        <span className='name'>{name}</span>
        <span className='quantity'>
            <div className='decrease-quantity' onClick={()=>decreseItemQuantity(cartItem)} >&#10092;</div>
             {quantity} 
            <div className='increase-quantity' onClick={()=>addItem(cartItem)} >&#10093;</div>

            </span>
        <span className='price'>{price}</span>
        <div className='remove-button' onClick={()=>removeItem(cartItem)}>&#10005;</div>

    </div>)
}


// const mapStateToProps = (state) => ({
//     cartItems: selectCartItems(state)
// })
const mapDispatchToProps = (dispatch) => ({
    removeItem: (item)=> dispatch(removeItem(item))
})
export default connect(null, mapDispatchToProps)(CheckoutItem)
