                <InputForm name='email' type='email' handleChange = {this.handleChange} ></InputForm>
                <InputForm name='password' type='password' handleChange = {this.handleChange} ></InputForm>

                <InputForm name='confirmPassword' type='password' handleChange = {this.handleChange}></InputForm> */}

                {/* <div className='singup-button'>
                    <SubmitButton color='black' className='' value='SIGN UP' handleSubmit={this.handleSubmit} />
                </div> */}





            </div>
        );
    }
}

export default SignUpForm
